import React, { useState } from 'react';
import { Button, Grid, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { ViewModule, TableChart } from '@mui/icons-material';

const GridTableViewToggle = () => {
  const [isGridView, setIsGridView] = useState(true);
  const toggleView = () => {
    setIsGridView(prevState => !prevState);
  };

  const items = [
    { id: 1, name: 'Item 1' },
    { id: 2, name: 'Item 2' },
    { id: 3, name: 'Item 3' },
  ];

  return (
    <div>
      <Button variant="outlined" onClick={toggleView} startIcon={isGridView ? <TableChart /> : <ViewModule />}>
        Toggle View
      </Button>
      {isGridView ? (
        <Grid container spacing={2}>
          {items.map(item => (
            <Grid item xs={4} key={item.id}>
              <Paper elevation={3} style={{ padding: '10px', textAlign: 'center' }}>
                {item.name}
              </Paper>
            </Grid>
          ))}
        </Grid>
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Name</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {items.map(item => (
                <TableRow key={item.id}>
                  <TableCell>{item.id}</TableCell>
                  <TableCell>{item.name}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </div>
  );
};

export default GridTableViewToggle;
