import React, { useState } from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import { useMediaQuery, useTheme } from '@mui/material';

const SideMenu = () => {
  const theme = useTheme();
  const isHalfWidth = useMediaQuery(theme.breakpoints.down('md')); // Check if the screen width is less than 'md' breakpoint (960px)

  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const toggleDrawer = (isOpen) => {
    setIsDrawerOpen(isOpen);
  };

  const drawerAnchor = isHalfWidth ? 'top' : 'left';

  return (
    <div>
      {/* Open side menu button */}
      <IconButton onClick={() => toggleDrawer(true)}>
        <MenuIcon />
      </IconButton>

      {/* Side Menu (Drawer) */}
      <Drawer anchor={drawerAnchor} open={isDrawerOpen} onClose={() => toggleDrawer(false)}>
        <List>
          <ListItem button onClick={() => console.log('Menu Item 1 clicked')}>
            <ListItemText primary="Menu Item 1" />
          </ListItem>
          <ListItem button onClick={() => console.log('Menu Item 2 clicked')}>
            <ListItemText primary="Menu Item 2" />
          </ListItem>
          <ListItem button onClick={() => console.log('Menu Item 3 clicked')}>
            <ListItemText primary="Menu Item 3" />
          </ListItem>
        </List>
      </Drawer>
    </div>
  );
};

export default SideMenu;
