import React, {useState} from 'react'
import { Card, CardContent, CardHeader, Typography, IconButton } from '@mui/material'
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
function IndexStatus({data}) {
    const [expanded, setExpanded] = useState(true);
    const handleCardClick = () => {
        setExpanded(!expanded);
    };
  return (
    <Card variant="outlined"
    style={{
        padding: 10,
        margin: 10,
        display: 'flex',
        flexDirection: 'column',
        height: expanded ? 'auto' : '70px',
        transition: 'height 0.3s',
        cursor: 'pointer',
    }}
    onClick={handleCardClick}>
        <CardHeader 
        title={data['Symbol']}
        action={
            <IconButton onClick={handleCardClick}>
              <ExpandMoreIcon />
            </IconButton>
          }>
            
        </CardHeader>
            <CardContent style={{
                flex: 1,
                // overflowY: 'auto',

            }}>
                <Typography variant='h6'>{data['Current Price']}</Typography>
                <Typography>{data['Last Updated Time']}</Typography>
            </CardContent>
        
    </Card>
  )
}

export default IndexStatus
