import React, {useState, useEffect, memo} from 'react'
import Typography from '@mui/material/Typography';
import SideBarComponent from './SideBarComponent';
import './Recommender.css'
import StocksTableView from './StocksTableView';
import { Card } from '@mui/material';

const baseUrl = 'http://localhost:8080/';

function Recommender() {
    const [index, setIndex] = useState('')
    const [display, setDisplay] = useState(true)
    const [indexStatus, setIndexStatus] = useState(null)

    const updateIndex = (index) => {
        setIndex(index);
        setDisplay(false);
    };

    const fetchData = async () => {
        // Fetch data from the current API URL
        try {
          const response = await fetch(urlStocks);
          const jsonData = await response.json();
          setData(jsonData);
          setSortedData(jsonData);
          console.log("stocks fetched successfully")
        } catch (error) {
          console.error('Error fetching data:', error);
        }
  
        try {
          const response = await fetch(urlIndex);
          const jsonData = await response.json();
          setIndexStatus(jsonData)
          setGotStocks(true);
          setShouldRender(true);
          console.log("index details fetched successfully");
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };
      
    return (
        <div className='recommender-container'>
            <div style={{
                display: 'flex',
                flexDirection: 'row',
                flex: '30%',
                marginRight: '20px'
            }}>
            <SideBarComponent updateIndex={updateIndex}/>
            <Card>

            </Card>
            </div>
            {display && (
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    flex: '65%',
                }}>
                    <Typography variant="h3" color="text.secondary" gutterBottom
                    style={{
                        textAlign: 'center',
                        marginTop: 20,
                        marginBottom: 20,
                        color: 'black',
                    }}>
                        Arbitrage Recommender
                    </Typography>
                    <Typography variant='h6'
                    style={{
                        textAlign: 'center',
                        marginTop: 20,
                        marginBottom: 20,
                        color: 'grey',
                        minWidth: '100px',
                    }}>Choose the index and get recommendations based on that index.</Typography>
                </div>
            )}

            {!display && (
                <div>
                <StocksTableView urlIndex={baseUrl+'indexStatus/'+index} urlStocks={baseUrl+index} index={index}/>
                </div>
            )}
        </div>
  )
}

export default memo(Recommender)
