import React from 'react';
import { Container, Typography, Link } from '@mui/material';
import './Footer.css'; // Import your CSS file
import { FormatBold } from '@mui/icons-material';

const Footer = ({ showFooter }) => {
  return (
    <footer className={`footer ${showFooter ? 'show-footer' : ''}`}>
      <Container maxWidth="lg">
        <Typography variant="body2" color="textSecondary" align="center" fontWeight={Bold}>
          &copy; {new Date().getFullYear()} Arbitrage Recommendation System.
        </Typography>
        <div className="footer-links">
          <Link href="/about">About Us</Link><br/>
          <Link href="/recommender">Recommender</Link>
        </div>
      </Container>
    </footer>
  );
};

export default Footer;
