import React, {useState} from 'react'
import { Card, CardContent, CardHeader, Typography, IconButton } from '@mui/material'
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
function IndexStatus({data}) {
    const [expanded, setExpanded] = useState(true);
    const handleCardClick = () => {
        setExpanded(!expanded);
    };
  return (
    <Card variant="outlined"
    style={{
        padding: 10,
        margin: 10,
        display: 'flex',
        flexDirection: 'column',
        height: expanded ? 'auto' : '70px',
        transition: 'height 0.3s',
        cursor: 'pointer',
        width: '50%'
    }}
    onClick={handleCardClick}>
        <CardHeader 
        title={data['index']}
        action={
            <IconButton onClick={handleCardClick}>
              {!expanded && (<ExpandMoreIcon />)}
              {expanded && (<ExpandLessIcon />)}
            </IconButton>
          }
        style={{
            fontSize: 20
        }}>
            
        </CardHeader>
            <CardContent style={{
                flex: 1,
                // overflowY: 'auto',
                display: 'flex',
                flexDirection: 'column',
                gap: 20
            }}>
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    // gap: 20
                }}>
                    <Typography variant='h6' style={{
                        color: 'grey',

                    }}>Price (INR)</Typography>
                    <Typography variant='h6'>{data['currentPrice']}</Typography>
                </div>

                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    // gap: 20
                }}>
                    <Typography variant='h6' style={{
                        color: 'grey',

                    }}>Last Updated Time</Typography>
                    <Typography variant='h6'>{data['lastUpdatedTime']}</Typography>
                </div>   
            </CardContent>
        
    </Card>
  )
}

export default IndexStatus
