import { Typography, TextField, Button } from '@mui/material'
import React, { useState } from 'react'
import { memo } from 'react'
import './Home.css'
import SearchIcon from '@mui/icons-material/Search';
import StockDetails from '../stock-details/StockDetails';



function Home() {
  const [symbol, setSymbol] = useState('')
  const [gotSymbol, setGotSymbol] = useState(false)

  const handleChange = (event) => {
    const inputText = event.target.value;
    setSymbol(inputText);
  }

  const handleSearch = (symbol) => {
    setGotSymbol(True)
  }

  return (
    <div className='home-container'>
      <div className='search-container'>
        <TextField 
        id="search-symbol" 
        label="Search by Symbol" 
        variant="outlined" 
        className='search-symbol'
        value={symbol}
        onChange={handleChange}/>
        <Button 
        variant="contained"
        color="primary"
        startIcon={<SearchIcon />}
        style={{flex: 'none',  marginLeft: '10px',  alignSelf: 'strech' }}
        onclick={handleSearch(symbol)}>Search</Button>
      </div>
      
      <div>
        {!gotSymbol && (
          <Typography variant='h3' gutterBottom>Stock Quote</Typography>
        )}

        {gotSymbol && (
          <StockDetails symbol={symbol}/>
        )}
      </div>
        
    </div>
  )
}

export default memo(Home)
